import React, { Component } from 'react';
import { Link } from 'react-router-dom';

const { organisations, user, sessionId } = props

class Organisations extends Component {
  constructor(props) {
    super(props)
    // BINDING GOES HERE
  }

  render() {
    // const { organisations, user, session } = props

    return(
      <div className='organisations'>
        <ul>
          <React.Fragment>
            {
              this.props.organisations.map((organisation, index) => {
                return(
                  <li key={index}>
                    {organisation.name}
                    <Link to={{ pathname: '/edit', state: { organisation, user, sessionId }}}>
                      Edit
                    </Link>
                    <Link to='/home'>
                      Join
                    </Link>
                  </li>
                )
              })
            }
          </React.Fragment>
        </ul>
      </div>
    )
  }
}

export default Organisations;
