import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const baseAPI = `http://localhost:3000`;

class Organisations extends Component {
  constructor(props) {
    super(props)
    // BINDING GOES HERE
    this.handleJoin = this.handleJoin.bind(this)
  }

  handleJoin(organisationId) {

    const { sessionId } = this.props.sessionId
    console.log('joining organisation')
    axios.post(baseAPI + `/organisations/join`, { organisationId }, {
      headers: {
        'Authorization': { sessionId }
      }
    })
    .then(response => {
      const { organisations } = this.props
      const organisation = organisations.find(organisation => organisation.id === organisationId)
      this.setState({ organisation })
      this.props.history.push(`/home`)
    })
  }

  componentDidMount() {
    const { organisationId } = this.props.organisation.id
  }

  render() {
    // const { organisations, user, session } = props

    return(
      <div className='organisations'>
        <ul>
          <React.Fragment>
            {
              this.props.organisations.map((organisation, index) => {
                return(
                  <li key={index}>
                    {organisation.name}
                    <Link to={{
                      pathname: '/edit',
                      state: {
                        organisation: this.props.organisation,
                        user: this.props.user,
                        sessionId: this.props.sessionId
                      }
                    }}>
                      Edit
                    </Link>
                    <Link to='/home'>
                      Join
                    </Link>
                  </li>
                )
              })
            }
          </React.Fragment>
        </ul>
      </div>
    )
  }
}

export default Organisations;
