import React, { Component } from 'react';
import axios from 'axios';

const baseAPI = `http://localhost:3000`;

class Home extends Component {
  constructor(props) {
    super(props)
    this.state = {
      user: {},
      sessionId: ''
    }
    // BINDING GOES HERE
  }

  componentDidMount() {
    if(this.props.location.state) {
      console.log('fetching user')
      this.setState({
        sessionId: this.props.location.state.sessionId
      })
      console.log('session id set')
      axios.get(baseAPI + `/users/me`, {
        headers: {
          'Authorization': this.state.sessionId
        }
      })
      .then(response => {
        console.log(response)
        this.setState({
          user: response.data
        })
      })
    }
  }

  render() {
    return(
      <div className='home'>
        <h2> Home </h2>
        <p>
          Logged in as
        </p>
      </div>
    )
  }
}

export default Home;
