import React, { Component } from 'react';
import axios from 'axios';

const baseAPI = `http://localhost:3000`;

class Shifts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      shifts: [],
      sessionId: this.props.location.state.sessionId,
      user: this.props.location.state.user,
      users: []
    }
    // BINDING GOES HERE
    this.fetchShifts = this.fetchShifts.bind(this)
  }

  fetchShifts() {
    console.log('fetching shifts')
    if(this.props.location.state) {
      const { sessionId } = this.props.location.state
      this.setState({ sessionId })
      axios.get(baseAPI + `/shifts`, {
        headers: {
          'Authorization': this.state.sessionId
        }
      })
      .then(response => {
        console.log(response)
        this.setState({
          shifts: response.data
        })
        console.log('fetching users')
        return axios.get(baseAPI + `/users`, {
          headers: {
            'Authorization': this.state.sessionId
          }
        })
        .then(response => {
          console.log(response)
          this.setState({
            users: response.data
          })
        })
      })
    }
  }

  handleSubmit(event) {
    event.preventDefault()
    console.log('adding shift')
    axios.post(baseAPI + `/shifts`, {
      userId: this.props.location.state.user.id,
      start: event.target.date.value + ' ' + event.target.start.value,
      finish: event.target.date.value + ' ' + event.target.finish.value,
      breakLength: event.target.break.value
    }, {
      headers: {
        'Authorization': this.props.location.state.sessionId
      }
    })
    .then(
      this.fetchShifts(),
    )
    console.log('shift added')
  }

  componentDidMount() {
    this.fetchShifts()
  }

  render() {
    return(
      <div className='shifts'>
        <p>
          Logged in as {this.props.location.state.user.name}.
          <button onClick={this.props.handleLogout}>
            Log out
          </button>
        </p>

        <h2>{this.props.location.state.organisation.name}</h2>

        <h4> Shifts </h4>
        <table>
          <thead>
            <tr>
              <th> Employee name </th>
              <th> Shift date </th>
              <th> Start time </th>
              <th> Finish time </th>
              <th> Break length (minutes) </th>
              <th> Hours worked </th>
              <th> Shift cost </th>
            </tr>
          </thead>
        </table>

        <h4> Add Shift </h4>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="date">
            Shift date:
          </label>
          <input
            type="date"
            name="date"
            id="date"
          />
          <br />
          <label htmlFor="start">
            Start time:
          </label>
          <input
            type="time"
            name="start"
            id="start"
          />
          <br />
          <label htmlFor="finish">
            Finish time:
          </label>
          <input
            type="time"
            name="finish"
            id="finish"
          />
          <br />
          <label htmlFor="Break length (minutes)">
            Break length (minutes)
          </label>
          <input
            type="text"
            name="break"
            id="break"
          />
          <br />
          <button type="submit">
            Submit
          </button>
        </form>
      </div>
    )
  }
}

export default Shifts;
