  // }

  render() {
    // const { organisations, user, session } = props

    return(
      <div className='organisations'>
        <ul>
          <React.Fragment>
            {
              this.props.organisations.map((organisation, index) => {
                return(
                  <li key={index}>
                    {organisation.name}
                    <Link to={{
                      pathname: '/edit',
                      state: {
                        organisation: this.props.organisation,
                        user: this.props.user,
                        sessionId: this.props.sessionId
                      }
                    }}>
                      Edit
                    </Link>
                    <Link to='/home' onClick={() => this.handleJoin(organisation.id)}>
                      Join
                    </Link>
                  </li>
                )
              })
            }
          </React.Fragment>
        </ul>
      </div>
    )
  }
}

export default Organisations;
